import { useEffect } from "react";
import axiosInstance from '../api/axiosInstance';
import './Restaurant.css';
import RestaurantCard from './RestaurantCard.jsx';
import { Container, Col, Button } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { setRestaurants } from '../redux/restaurantsSlice';   


const Restaurants = () => {
    const dispatch=useDispatch();
    const restaurantsAvailable = useSelector((state) => state.restaurants);
    const [seeRestaurantsAvailable, setSeeRestaurantsAvailable] = useState(false);

    useEffect(() => {        
        const fetchData = async () => {
            try {
                const response = await axiosInstance.get('/restaurants', {
                    params: { limit: 10, offset: 0 }
                });
                dispatch(setRestaurants(response.data));
            } catch (error) {
                console.error('Error fetching restaurants:', error);
            }
        }
        fetchData();
    }, [dispatch]);

    return (
        <Container className="restaurant-container">
            <Col>Restaurants Component</Col>
            <Button className="btn-restaurants" onClick={() => setSeeRestaurantsAvailable(!seeRestaurantsAvailable)}>
                {seeRestaurantsAvailable ? 'Hide' : 'Show'} Available Restaurants
            </Button>

            {seeRestaurantsAvailable && restaurantsAvailable.map((restaurant) => (
                <Container key={restaurant.id}>
                    <RestaurantCard restaurant={restaurant} />
                </Container>
            ))}
        </Container>
    );
}

export default Restaurants;
