import { use, useEffect, useState } from "react";
import axiosInstance from '../api/axiosInstance';
import './RestaurantCard.css';
import { Container, Col, Card, Button, Form} from 'react-bootstrap';


const RestaurantCard = ({restaurant}) => {
    const [restaurantsAvailability, setRestaurantsAvailability] = useState([]);
    const [seeRestaurantsAvailability, setSeeRestaurantsAvailability] = useState(false);
    const [selectedAvailability, setSelectedAvailability] = useState(null);
    const [personaName, setPersonaName] = useState(null);

    useEffect(() => {        
        async function fetchData() {
            try {
                const response = await axiosInstance.get('/restaurants/availability', {
                    params: { limit: 10, offset: 0 }
                });
                console.log('Restaurants data:', response.data);
                setRestaurantsAvailability(response.data);
            } catch (error) {
                console.error('Error fetching restaurants:', error);
            }
        }
        fetchData();
    }, []);

    return (
        <Container className="restaurant-container">
            <Container key={restaurant.id}>
                <Col>{restaurant.name}</Col>
                <Col>{restaurant.description}</Col>
                <Button className="btn-card" onClick={() => setSeeRestaurantsAvailability(!seeRestaurantsAvailability)}> {seeRestaurantsAvailability? 'Ocultar disponibilidad':'Ver disponibilidad'}</Button>
                {seeRestaurantsAvailability && restaurantsAvailability.filter((a) => a.restaurant_id === restaurant.id && !a.reserved).map((availability) => (
                    <Container>
                    <Card key={availability.id}>    
                        <Form.Check
                            type="checkbox"
                            id={`availability-${availability.id}`}
                            label={`Fecha y hora: ${availability.schedule_time} \n Reservado: ${availability.reserved ? 'Sí' : 'No'}`} 
                            disabled={availability.reserved} 
                            checked={selectedAvailability?.id === availability.id}  
                            onChange={() => setSelectedAvailability(availability)}
                        ></Form.Check>
                    </Card>
                    {selectedAvailability?.id===availability.id && (
                        <Container>
                            <Form.Control
                                type="text"
                                placeholder="Nombre de la persona"
                                value={personaName}
                                onChange={(e) => setPersonaName(e.target.value)} 
                            >   
                            </Form.Control>
                            <Button className="btn-card" onClick={async () => {
                                try {
                                    const scheduleDate = new Date(selectedAvailability.schedule_time);
                                    const formattedTime = scheduleDate.getFullYear() + '-' +
                                                        String(scheduleDate.getMonth() + 1).padStart(2, '0') + '-' +
                                                        String(scheduleDate.getDate()).padStart(2, '0') + ' ' +
                                                        String(scheduleDate.getHours()).padStart(2, '0') + ':' +
                                                        String(scheduleDate.getMinutes()).padStart(2, '0') + ':' +
                                                        String(scheduleDate.getSeconds()).padStart(2, '0');
                                    const response = await axiosInstance.put('/restaurants/reserve', {                      
                                    }   , {
                                        params: { 
                                            personName: personaName,                                        
                                            restaurantId: selectedAvailability.restaurant_id,
                                            scheduleTime: formattedTime
                                        }
                                    });
                                    console.log('Reservation response:', response.data);  
                                    alert('Reservation successful');                              
                                } catch (error) {
                                    alert('Error making reservation');
                                    console.error('Error making reservation:', error);
                                }   
                            }}>Reservar</Button>  
                        </Container>
                    )}
                    </Container> 
                ))}
            </Container>
        </Container>
    );
}

export default RestaurantCard;
