import { Container, Col, Button } from "react-bootstrap"; 
import { useState, useEffect } from "react";
import axiosInstance from '../api/axiosInstance';
import "./Tours.css";

const Tours = () => {   
    ///api/axiosInstance
    const [tours, setTours] = useState([]);
    const [seeTours, setSeeTours] = useState(false);

    useEffect(() => {   
        axiosInstance.get('/tours?limit=10&offset=0')
        .then(response => {
            setTours(response.data);
            console.log(response.data);
        })
        .catch(error => {
            console.error('There was an error!', error);
        });     
    }, []);

    return (
        <Container className="tour-container">
            <Col>Tours Component</Col>
            <Button onClick={()=>setSeeTours(!seeTours)}>{seeTours? 'Hide Tours':'See Tours'}</Button>
            {seeTours && (
                <Container>
                    {tours.map((tour) => (  
                        <div key={tour.id} className="tour-card">
                            <h3>{tour.name}</h3>
                            <p>{tour.description}</p>
                            <p>Price: ${tour.price}</p>
                            <p>Duration: {tour.duration} hours</p>
                        </div>
                    ))}
                </Container>
            )}
        </Container>
    );
}

export default Tours;
